sys_variable_value.value:
(function execute(inputs, outputs) {
  if(inputs.responseBody) {
  	var returnedObject = JSON.parse(inputs.responseBody);
  	if(returnedObject) {
      outputs.authsession = returnedObject.authSession;
    }
  }
})(inputs, outputs);

