(function execute(inputs, outputs) {
  if(inputs.responseBody) {
  	var returnedObject = JSON.parse(inputs.responseBody);
  	if(returnedObject) {
      outputs.apitoken = returnedObject.apiToken;
      outputs.refreshtoken = returnedObject.refreshToken;
    }
  }
})(inputs, outputs);

